mapper.update:
update user
set verify = #{verify}
where uid=#{uid};

update user
set img = #{img}
where uid=#{uid};

update user
set postcount = postcount+1
where uid=#{uid};

update user
set replycount = replycount+1
where uid=#{uid};

update user
set email = #{email}
where uid=#{uid};

update user
set upassword = #{password}
where uid=#{uid};

update user
set username = #{username}
where uid=#{uid};

update user
set describes = #{describe}
where uid=#{uid};

mapper.select:
select count(*)
from user
where email = #{email};

select count(*)
from user
where username = #{username};

select count(*)
from user
where email = #{email};

select uid
from user
where username = #{username} and upassword = #{upassword};
